        IEnumerable<(int, int)> lines = input.Split("\r\n")
            .Select(line => line.Split("   "))
            .Select(numbers => (Convert.ToInt32(numbers[0]), Convert.ToInt32(numbers[1])));

        List<int> left = [];
        List<int> right = [];

        foreach ((int left, int right) line in lines)
        {
            left.Add(line.left);
