    public override string SolvePartOne()
    {
        int total = 0;
        for (int i = 0; i < _left.Count; i++)
        {
            int leftNum = _left[i];
            int rightNum = _right[i];

            if (leftNum > rightNum)
            {
                total += leftNum - rightNum;
            }
            else
            {
                total += rightNum - leftNum;
            }
        }

        return total.ToString();
    }

    public override string SolvePartTwo()
    {
        int total = 0;

        foreach (int number in _left)
        {
            int occurrences = _right.Count(n => n == number);
            total += number * occurrences;
        }

        return total.ToString();
    }
