    public override string SolvePartOne()
    {
        int count = _lines
            .Count(line => line[0] != line[1] && GetErrorIndexOrDefault(line) == null);

        return count.ToString();
    }

    public override string SolvePartTwo()
    {
        int count = _lines
            .Count(line =>
            {
                int? firstError = GetErrorIndexOrDefault(line);

                if (firstError == null)
                {
                    return true;
                }

                // bruteforcing it cause it's past midnight and I cba (thanks hannah)
                for (int i = 0; i < line.Count; i++)
                {
                    List<int> lineCopy = line.ToList();
                    lineCopy.RemoveAt(i);

                    if (GetErrorIndexOrDefault(lineCopy) == null)
                    {
                        return true;
                    }
                }
                
                return false;
            });

        return count.ToString();
    }

    private static int? GetErrorIndexOrDefault(List<int> levels)
    {
        bool isIncreasing = levels[0] < levels[1];

        for (int i = 1; i < levels.Count; i++)
        {
            int level = levels[i];
            int previous = levels[i - 1];

            if (isIncreasing)
            {
                if (level <= previous || level > previous + 3)
                {
                    return i - 1;
                }
            }
            else
            {
                if (level >= previous || level < previous - 3)
                {
                    return i - 1;
                }
            }
        }

        return null;
    }
